import React, { useState, useRef } from 'react';
import { IoClose, IoLogoPython } from 'react-icons/io5';
import Login from './Login';
import BackDrop from '../BackDrop';

function SignUp({ setsignup }) {
  const [login, setlogin] = useState(false);

  const handleSubmit = event => {
    event.preventDefault();
    fetch(process.env.REACT_APP_API + 'users', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        UserName: event.target.UserName.value,
        UserEmail: event.target.Email.value,
        UserPassword: event.target.Password.value,
      }),
    })
      .then(res => res.json())
      .then(
        result => {
          alert(result);
          alert('Account created');
        },
        error => {
          alert('Failed');
        }
      );
  };

  const ref = useRef(null);

  const [userInfo, setuserInfo] = useState({
    username: '',
    email: '',
    password: '',
  });

  return (
    <>
      {login && (
        <BackDrop loading={false}>
          <Login setlogin={setlogin} />
        </BackDrop>
      )}
      <div class='py-12 px-12 bg-white rounded-2xl shadow-xl z-20 animate__animated animate__fadeInDown animate__faster'>
        <div
          onClick={() => setsignup(false)}
          className='flex justify-end h-12 '
        >
          <IoClose className='text-2xl text-gray-600  cursor-pointer' />
        </div>
        <div>
          <h1 class='text-3xl font-bold text-center mb-4 cursor-pointer'>
            Create An Account
          </h1>
          <p class='w-80 text-center text-sm mb-8 font-semibold text-gray-700 tracking-wide cursor-default'>
            Create an account to enjoy all the services without any ads!
          </p>
        </div>
        <form onSubmit={handleSubmit} ref={ref}>
          <div class='space-y-4'>
            <input
              type='text'
              placeholder='Full name'
              required
              aria-required='true'
              name='UserName'
              class='block text-sm py-3 px-4 rounded-lg w-full border outline-none'
            />
            <input
              type='email'
              placeholder='Email'
              class='block text-sm py-3 px-4 rounded-lg w-full border outline-none'
              required
              aria-required='true'
              name='Email'
            />
            <input
              type='password'
              placeholder='Password'
              name='Password'
              class='block text-sm py-3 px-4 rounded-lg w-full border outline-none'
              required
              aria-required='true'
            />
          </div>
        </form>
        <div class='text-center mt-6'>
          <button
            type='submit'
            class='block transition-all duration-300 ease-linear mx-auto shadow bg-blue-500 hover:bg-blue-600 focus:shadow-outline focus:outline-none text-white text-lg font-medium py-3 px-8 rounded-lg'
          >
            Create Account
          </button>
          <p
            onClick={() => {
              setlogin(true);
            }}
            class='mt-4 text-md  font-medium '
          >
            Already Have An Account?
            <span class='hover:underline cursor-pointer'> Sign In</span>
          </p>
        </div>
      </div>
    </>
  );
}

export default SignUp;
