import React, { useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper.min.css';
import moment from 'moment';
import SwiperCore, { Autoplay } from 'swiper';

SwiperCore.use([Autoplay]);

function Home() {
  const [movies, setMovies] = useState([]);
  const [category, setCategory] = useState([]);

  const refreshRow = () => {
    fetch(process.env.REACT_APP_API + 'categories')
      .then(response => response.json())
      .then(data => {
        setCategory(data);
      });
    fetch(process.env.REACT_APP_API + 'movies')
      .then(response => response.json())
      .then(data => {
        setMovies(data);
      });
  };

  useEffect(() => {
    refreshRow();
  }, [movies, category]);

  const posterImg = filename => {
    return process.env.REACT_APP_PHOTOPATH + filename;
  };

  return (
    <>
      {category.map(cat => {
        return (
          <div
            className='h-auto py-4 w-11/12 mx-auto border-4 border-gray-300 '
            id={cat.CategoryId}
          >
            <div className='text-black font-bold h-12 text-xl ml-7 flex items-center '>
              {cat.CategoryName}
            </div>
            <div className='h-auto w-full  mx-auto my-auto flex flex-row overflow-x-auto  overflow-y-hidden no-scrollbar  '>
              <Swiper spaceBetween={10} slidesPerView={6} navigation>
                {movies
                  .filter(movie => movie.Category === cat.CategoryName)
                  .map(movie => (
                    <SwiperSlide>
                      <div className=' rounded-xl h-auto w-auto  flex justify-items-start mx-4 my-4 cursor-pointer shadow-2xl hover:scale-105'>
                        <div className='container h-56 w-56  relative text-center text-white mx-auto rounded-xl'>
                          <img
                            src={posterImg(movie.PosterFileName)}
                            alt='poster'
                            className=' h-48 w-48 rounded-xl z-10 hover:mb-6  '
                          />
                          <div className='h-48 w-64 absolute bottom-0 bg-gradient-to-t from-black to-transparent z-20'></div>
                          <p className='bottom-7 text-base font-sans font-semibold absolute z-30 left-2'>
                            {movie.MovieName}
                          </p>
                          <div className=''>
                            <p className='bottom-1 left-2 text-md font-sans font-semibold absolute z-30 '>
                              <i className='bx bxs-star mr-1 text-yellow-400'></i>
                              {movie.Rating}
                            </p>
                            <p className='bottom-1 right-4 text-md font-sans font-semibold absolute z-30'>
                              <i className='bx bxs-calendar mr-1 text-blue-400'></i>
                              {moment(movie.ReleaseDate).format('ll')}
                            </p>
                            <p className='bottom-0 left-12 text-md font-sans font-semibold absolute z-30'></p>
                          </div>
                        </div>
                      </div>
                    </SwiperSlide>
                  ))}
              </Swiper>
            </div>
          </div>
        );
      })}
    </>
  );
}

export default Home;
