import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navigation from './Components/Admin/AdminNavigation';
import AdminCategory from './Components/Admin/AdminCategory';
import AdminMovie from './Components/Admin/AdminMovie';
import UserNavigation from './Components/User/UserNavigation';
import AboutMovie from './Components/User/AboutMovie';
import {
  BrowserRouter,
  Route,
  Switch,
  useHistory,
  withRouter,
} from 'react-router-dom';
import Home from './Components/User/Home';
function App({ history }) {
  const checkpage = location => {
    if (location.pathname === '/admin') {
      return false;
    }
    return true;
  };

  const [navbar, setNavbar] = useState(checkpage(history.location));
  history.listen(location => {
    const bool = checkpage(location);
    setNavbar(bool);
  });

  return (
    <div className='App'>
      <BrowserRouter>
        {navbar && <Navigation />}
        <Switch>
          <Route path='/' exact component={Home} />
          <Route path='/movie/:id' exact component={AboutMovie} />
          <Route path='/admin/movies' component={AdminMovie} />
          <Route path='/admin/categories' component={AdminCategory} />
        </Switch>
      </BrowserRouter>
    </div>
  );
}

export default App;
